scriptlet.script:
var script = "#!/bin/sh\n";
var delimiter = resolveValue("delimiter", ",");

// add predefined filters and scripts
var cleanupSystemFiles = resolveValue("cleanupSystemFiles", "No");
if (cleanupSystemFiles == "Yes") {
	// add predefined filters
	var filterValue = getValue("SystemFilters");
	if (filterValue != null) {
		var filters = filterValue.split("\n");
		for (var j = 0; j < filters.length; j++) {
			script += getDeleteScript(filters[j]);
			script += "\n";
		}
	}
}

// add custom filters
var cleanupPaths = resolveValue("cleanupPaths", "");
list = cleanupPaths.split(delimiter);
if (list.length != 0) {
	for (var i = 0; i < list.length; i++) {
		script += getDeleteScript(list[i]);
		script += "\n";
	}
}

// file/folders not found
script += "if [ $? -eq 1 ]\n";
script += "then exit 0\n";
script += "fi\n";
scriptletContext.put("deleteScript", script);

// Utility functions
function getDeleteScript(filter) {
	var script = "";
	if (filter != "") {
		script += "rm --recursive --force --verbose " + filter + "\n";
	}
	return script;
}

function resolveValue(inputName, defaultValue) {
	var value = getValue(inputName);
	if (value == null) {
		value = defaultValue;
		scriptletContext.put(inputName, value);
	}
	return value;
}

function getValue(inputName) {
	if (typeof (scriptletContext.get(inputName)) != "undefined" && scriptletContext.get(inputName) != "") {
		return scriptletContext.get(inputName);
	}
	return null;
}

var units = ["bytes", "KB", "MB", "GB", "TB", "PB"]; //not standard

var difference = scriptletRawResult["result"];
var negative = (difference < 0);
difference = Math.abs(difference);
var i = 0;
while (difference > 1024 && i < 5) {
	difference = difference / 1024;
	i++;
}
difference = Math.round(difference * 100) / 100;
if (negative) {
	scriptletContext.put("difference_with_unit", "-" + difference + " " + units[i]);
} else {
	scriptletContext.put("difference_with_unit", "" + difference + " " + units[i]);
}
