scriptlet.script:
try {
	var randomScriptName = getValue("randomScriptName") + ".sh";

	// SOURCE inputs
	var sourceHost = resolveHost("sourceHost", "127.0.0.1");
	var sourceUsername = getValue("sourceUsername");
	var sourcePassword = getValue("sourcePassword");

	// the input sourceScriptPath is required
	var sourceScriptPath = getValue("sourceScriptPath");
	if (sourceScriptPath == null) {
		throw "The input sourceScriptPath is required.";
	}

	// TARGET inputs
	var targetHost = resolveHost("targetHost", sourceHost);
	var targetUsername = resolveValue("targetUsername", sourceUsername);
	var targetPassword = resolveValue("targetPassword", sourcePassword);
	var targetWorkingDirectory = resolveValue("targetWorkingDirectory", "/tmp");

	var equalsHosts = (sourceHost == targetHost);
	scriptletContext.put("equalsHosts", "" + equalsHosts);

	if (equalsHosts) {
		/* 
		Commands to be run by the execute script step:
		- call the local script file
		*/
		var script = getSudoPermission(targetUsername, targetPassword) + "sh \"" + sourceScriptPath + "\"";
		scriptletContext.put("executeScript", script);
	} else {
		var targetScriptPath = targetWorkingDirectory + "/" + randomScriptName;
		scriptletContext.put("targetScriptPath", targetScriptPath);

		/* 
		Commands to be run by the execute script step:
		- call the remote script file
		*/
		var script = getSudoPermission(targetUsername, targetPassword) + "sh \"" + targetScriptPath + "\"";
		scriptletContext.put("executeScript", script);

		// Delete temp script
		script = "rm -f " + targetScriptPath;
		scriptletContext.put("targetDeleteScript", script);
	}
} catch (e) {
	scriptletContext.put("fail", "true");
	scriptletContext.put("FailureMessage", "" + e);
}

// Utility functions
function getSudoPermission(user, password) {
	var script = "";
	if (user != null) {
		if (password != null) {
			script += "echo \"" + password + "\" | ";
		}
		script += "sudo -S -u " + user + " ";
	}
	return script;
}

function resolveHost(inputName, defaultValue) {
	var value = getValue(inputName);
	if (value == null) {
		value = defaultValue;
		scriptletContext.put(inputName, value);
	} else if (value.toLowerCase() == "localhost") {
		value = "127.0.0.1";
		scriptletContext.put(inputName, value);
	}
	return value;
}

function resolveValue(inputName, defaultValue) {
	var value = getValue(inputName);
	if (value == null) {
		value = defaultValue;
		scriptletContext.put(inputName, value);
	}
	return value;
}

function getValue(inputName) {
	if (typeof (scriptletContext.get(inputName)) != "undefined" && scriptletContext.get(inputName) != "") {
		return scriptletContext.get(inputName);
	}
	return null;
}
